import { useSelector } from "react-redux";
import "./css/style.css"
let Hero = () => {

    let i=0;
    setInterval(() => {
        let span=document.querySelector(".typed");
        let list=span.getAttribute("data-typed-items").split(",");
        if(i===list.length) i=0;
        span.innerText=list[i];
        i++;
    }, 800);
    let img= useSelector(state=>state.userImage);
    return (
        <>
        <div className="edit" style={{ backgroundImage: `url(${img})` ,backgroundRepeat: no-Repeat ,backgroundSize: cover }}>
            <section id="hero" class="d-flex flex-column justify-content-center align-items-center">
                <div class="hero-container" data-aos="fade-in">
                    <h1>Alex Smith</h1>
                    <p>I'm <span class="typed" data-typed-items="Designer, Developer, Freelancer, Photographer"></span></p>
                </div>
            </section>
        </div>
        </>
            )
}

export default Hero;
