import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Home from "./components/home";

import Login from "./components/login";
import Navbar from "./components/navbar";
import PersonalData from "./components/personalData";
import PublicPreview from "./components/publicPreview";
import Qualifications from "./components/qualifications";
import SignUp from "./components/signup";
import { auth, firestore } from "./firebase";
import { userCreator } from "./redux/actions/userActions";
import ImageUploading from "./components/imageUploading"
import EducationInfo from "./components/educationInfo"
import Resume from "./components/resume"
import SocialHandle from "./components/socialHandle"
import A from "./"
let App = () => {
  let dispatch = useDispatch();

  useEffect(() => {
    let unsub = auth.onAuthStateChanged(async (user) => {
      dispatch(userCreator(user));
      if (user) {
        let { uid, email } = user;
        let docRef = firestore.collection("users").doc(uid);

        let doc = await docRef.get();

        if (!doc.exists) {
          docRef.set({
            email,
          });
        }
      }
    });

    return () => {
      unsub();
    };
  }, []);

  return (
    <>
      <Router>
        <Navbar />
        <Switch>
          <Route path="/publicpreview/:rid">
            <PublicPreview />
          </Route>
          <Route path="/educationInfo">
            <EducationInfo/>
          </Route>
          <Route path="/socialHandle">
            <SocialHandle/>
          </Route>
          <Route path="/A">
            <SocialHandle/>
          </Route>
          <Route path="/resume">
            <Resume/>
          </Route>
          <Route path="/userImg">
            <ImageUploading/>
          </Route>
          <Route path="/qualifications">
            <Qualifications />
          </Route>
          <Route path="/personal">
            <PersonalData />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/signup">
            <SignUp />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
      </Router>
    </>
  );
};

export default App;
